import React from "react";
import { Button } from "../components/ui/button";
import { Card, CardHeader, CardTitle, CardContent } from "../components/ui/card";
import { Users } from "lucide-react";
import { Link } from "react-router-dom";
import Layout from "../components/Layout";

export default function Home() {
  return (
    <Layout>
      {/* Animated background shapes */}
      <div className="fixed inset-0 -z-10 overflow-hidden">
        <div className="absolute top-[-120px] left-[-120px] w-[300px] h-[300px] rounded-full bg-purple-400 opacity-30 blur-3xl animate-float-slow" />
        <div className="absolute bottom-[-100px] right-[-80px] w-[240px] h-[240px] rounded-full bg-pink-400 opacity-25 blur-2xl animate-float-fast" />
        <div className="absolute top-1/2 left-1/2 w-[200px] h-[200px] rounded-full bg-blue-400 opacity-20 blur-2xl animate-float-mid" style={{transform:'translate(-50%,-50%)'}} />
      </div>
      <section className="w-full min-h-[80vh] flex flex-col items-center justify-center py-16 px-2">
        {/* Glassmorphic hero card */}
        <div className="backdrop-blur-lg bg-white/60 rounded-3xl shadow-2xl border border-white/40 max-w-xl w-full mx-auto flex flex-col items-center py-12 px-6 relative animate-fade-in">
          {/* Floating Chat Icon */}
          <div className="absolute -top-14 left-1/2 -translate-x-1/2">
            <div className="bg-gradient-to-br from-blue-500 via-purple-400 to-pink-400 rounded-full p-4 shadow-xl animate-float">
              <svg width="56" height="56" viewBox="0 0 56 56" fill="none" xmlns="http://www.w3.org/2000/svg" className="drop-shadow-lg">
                <ellipse cx="28" cy="28" rx="24" ry="22" fill="#fff"/>
                <rect x="16" y="18" width="24" height="14" rx="7" fill="#a78bfa"/>
                <ellipse cx="22" cy="25" rx="2" ry="2" fill="#fff"/>
                <ellipse cx="28" cy="25" rx="2" ry="2" fill="#fff"/>
                <ellipse cx="34" cy="25" rx="2" ry="2" fill="#fff"/>
              </svg>
            </div>
          </div>
          <h1 className="mt-8 text-4xl sm:text-5xl font-extrabold text-center text-transparent bg-clip-text bg-gradient-to-r from-blue-700 via-purple-700 to-pink-600">
            AnonChat
          </h1>
          <p className="mt-3 text-lg sm:text-xl text-gray-700 font-medium text-center">
            No database. No chat storage. Anyone can chat, anytime.
          </p>
          <Link to="/chat" className="mt-8">
            <Button className="text-xl px-8 py-4 rounded-full shadow-xl bg-gradient-to-r from-blue-600 to-pink-500 hover:scale-105 transition-transform animate-fade-in">
              Start Chatting
            </Button>
          </Link>
        </div>
        {/* Feature highlights */}
        <div className="mt-16 flex flex-col md:flex-row gap-6 w-full max-w-4xl justify-center items-center animate-fade-in">
          <div className="flex-1 min-w-[220px] bg-white/80 rounded-2xl shadow-md px-7 py-7 flex flex-col items-center border-t-4 border-blue-400">
            <svg className="w-10 h-10 mb-3 text-blue-500" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path d="M17 20h5v-2a4 4 0 0 0-4-4H6a4 4 0 0 0-4 4v2h5"/><circle cx="12" cy="7" r="4"/></svg>
            <div className="text-lg font-bold mb-1">No Account Needed</div>
            <div className="text-gray-500 text-center">Jump in instantly. No registration, just share a room key.</div>
          </div>
          <div className="flex-1 min-w-[220px] bg-white/80 rounded-2xl shadow-md px-7 py-7 flex flex-col items-center border-t-4 border-purple-400">
            <svg className="w-10 h-10 mb-3 text-purple-500" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><rect x="3" y="11" width="18" height="10" rx="5"/><path d="M7 11V7a5 5 0 0 1 10 0v4"/></svg>
            <div className="text-lg font-bold mb-1">Private & Secure</div>
            <div className="text-gray-500 text-center">Chats are ephemeral and private. No storage, no tracking.</div>
          </div>
          <div className="flex-1 min-w-[220px] bg-white/80 rounded-2xl shadow-md px-7 py-7 flex flex-col items-center border-t-4 border-pink-400">
            <svg className="w-10 h-10 mb-3 text-pink-500" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><circle cx="12" cy="12" r="10"/><path d="M8 12h8"/><path d="M12 8v8"/></svg>
            <div className="text-lg font-bold mb-1">Modern Experience</div>
            <div className="text-gray-500 text-center">Beautiful, fast, and responsive chat for everyone.</div>
          </div>
        </div>
      </section>
      {/* Animations CSS */}
      <style>{`
        .animate-float {
          animation: floatY 3.5s ease-in-out infinite;
        }
        @keyframes floatY {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-18px); }
        }
        .animate-float-slow {
          animation: floatX 24s ease-in-out infinite alternate;
        }
        .animate-float-fast {
          animation: floatX 12s ease-in-out infinite alternate;
        }
        .animate-float-mid {
          animation: floatX 18s ease-in-out infinite alternate;
        }
        @keyframes floatX {
          0% { transform: translateY(0) scale(1); }
          50% { transform: translateY(-30px) scale(1.06); }
          100% { transform: translateY(0) scale(1); }
        }
      `}</style>
    </Layout>
  );
}
