import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import { ChatProvider } from './context/ChatContext';
import HomeScreen from './components/HomeScreen';
import ChatRoom from './components/ChatRoom';
import Home from './pages/Home';
import Contact from './pages/Contact';

function AppRoutes() {
  const [inRoom, setInRoom] = useState(() => {
    const user = localStorage.getItem('anonchat_user');
    const roomKey = localStorage.getItem('anonchat_roomKey');
    return Boolean(user && roomKey);
  });
  const navigate = useNavigate();

  useEffect(() => {
    const user = localStorage.getItem('anonchat_user');
    const roomKey = localStorage.getItem('anonchat_roomKey');
    if (user && roomKey) {
      setInRoom(true);
      navigate('/chat', { replace: true });
    }
  }, []);

  return (
    <Routes>
      <Route path="/" element={<Home />} />
      <Route path="/contact" element={<Contact />} />
      <Route path="/chat" element={inRoom ? <ChatRoom exitRoom={() => {
        setInRoom(false);
        localStorage.removeItem('anonchat_user');
        localStorage.removeItem('anonchat_roomKey');
        navigate('/', { replace: true });
      }} /> : <HomeScreen enterRoom={() => {
        setInRoom(true);
        navigate('/chat', { replace: true });
      }} />} />
    </Routes>
  );
}

function App() {
  return (
    <ChatProvider>
      <Router>
        <AppRoutes />
      </Router>
    </ChatProvider>
  );
}

export default App;
