import React, { useEffect, useRef, useState } from 'react';
import { useChat } from '../context/ChatContext';
import { io } from 'socket.io-client';

let socket;

function ChatRoom({ exitRoom }) {
  const { user, roomKey, setUser, setRoomKey } = useChat();
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [users, setUsers] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket = io('http://localhost:5002');
    socket.emit('join_room', { username: user, roomKey });
    socket.on('chat_history', msgs => setMessages(msgs));
    socket.on('receive_message', msg => setMessages(msgs => [...msgs, msg]));
    socket.on('user_joined', ({ username }) => setUsers(u => [...u, username]));
    socket.on('user_left', ({ username }) => setUsers(u => u.filter(name => name !== username)));
    return () => {
      socket.emit('leave_room', { roomKey, username: user });
      socket.disconnect();
    };
  }, [roomKey, user]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = e => {
    e.preventDefault();
    if (!input.trim()) return;
    socket.emit('send_message', { roomKey, username: user, message: input });
    setInput('');
  };

  const handleExit = () => {
    setUser(null);
    setRoomKey(null);
    exitRoom();
  };

  return (
    <div className="bg-white w-full max-w-lg p-6 rounded shadow-md flex flex-col">
      <div className="flex justify-between items-center mb-4">
        <div className="font-semibold">Room Key: <span className="font-mono">{roomKey}</span></div>
        <button className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600" onClick={handleExit}>Exit Chat</button>
      </div>
      <div className="flex-1 overflow-y-auto mb-4 bg-gray-50 rounded p-2" style={{ minHeight: 300 }}>
        {messages.map((msg, i) => (
          <div key={i} className={`mb-2 ${msg.username === user ? 'text-right' : 'text-left'}`}>
            <span className="font-bold text-blue-600">{msg.username}:</span> <span>{msg.message}</span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <form className="flex gap-2" onSubmit={sendMessage}>
        <input
          className="flex-1 border rounded px-2 py-1"
          placeholder="Type a message..."
          value={input}
          onChange={e => setInput(e.target.value)}
        />
        <button className="bg-blue-500 text-white px-4 py-1 rounded hover:bg-blue-600" type="submit">Send</button>
      </form>
    </div>
  );
}

export default ChatRoom;
