import React, { useState } from 'react';
import { useChat } from '../context/ChatContext';

function HomeScreen({ enterRoom }) {
  const { setUser, setRoomKey } = useChat();
  const [showJoin, setShowJoin] = useState(false);
  const [showCreate, setShowCreate] = useState(false);
  const [username, setUsername] = useState('');
  const [roomInput, setRoomInput] = useState('');
  const [error, setError] = useState('');

  const handleCreateRoom = async () => {
    setError('');
    const res = await fetch('http://localhost:5002/create-room', { method: 'POST' });
    const data = await res.json();
    setRoomKey(data.roomKey);
    setShowCreate(true);
  };

  const handleJoinRoom = async () => {
    if (!username || !roomInput) {
      setError('Username and Room Key required');
      return;
    }
    // Validate room key exists
    const res = await fetch(`http://localhost:5002/validate-room/${roomInput}`);
    const data = await res.json();
    if (!data.exists) {
      setError('Room key does not exist!');
      return;
    }
    setUser(username);
    setRoomKey(roomInput);
    enterRoom();
  };

  return (
    <div className="bg-white p-8 rounded shadow-md w-80">
      <h1 className="text-2xl font-bold mb-6 text-center">Chat App</h1>
      <button
        className="w-full bg-blue-500 text-white py-2 rounded mb-4 hover:bg-blue-600"
        onClick={handleCreateRoom}
      >
        Create Room
      </button>
      <button
        className="w-full bg-green-500 text-white py-2 rounded mb-4 hover:bg-green-600"
        onClick={() => setShowJoin(true)}
      >
        Join Room
      </button>
      {showCreate && (
        <div className="mt-4 text-center">
          <div className="font-semibold">Room Created!</div>
          <div className="text-lg font-mono bg-gray-100 rounded p-2 mt-2">{useChat().roomKey}</div>
          <input
            className="mt-4 w-full border rounded px-2 py-1"
            placeholder="Enter username"
            value={username}
            onChange={e => setUsername(e.target.value)}
          />
          <button
            className="w-full bg-blue-500 text-white py-2 rounded mt-2 hover:bg-blue-600"
            onClick={() => {
              if (!username) {
                setError('Username required');
                return;
              }
              setUser(username);
              enterRoom();
            }}
          >
            Enter Chat
          </button>
        </div>
      )}
      {showJoin && (
        <div className="mt-4">
          <input
            className="w-full border rounded px-2 py-1 mb-2"
            placeholder="Enter username"
            value={username}
            onChange={e => setUsername(e.target.value)}
          />
          <input
            className="w-full border rounded px-2 py-1 mb-2"
            placeholder="Enter room key"
            value={roomInput}
            onChange={e => setRoomInput(e.target.value)}
          />
          <button
            className="w-full bg-green-500 text-white py-2 rounded hover:bg-green-600"
            onClick={handleJoinRoom}
            type="button"
          >
            Join
          </button>
        </div>
      )}
      {error && <div className="text-red-500 mt-2 text-center">{error}</div>}
    </div>
  );
}

export default HomeScreen;
