import React from "react";
import { Link, useLocation } from "react-router-dom";

export default function Layout({ children }) {
  const { pathname } = useLocation();
  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-blue-100 to-white">
      <header className="sticky top-0 z-30 w-full bg-white/80 backdrop-blur border-b border-blue-100 shadow-sm">
        <div className="max-w-6xl mx-auto flex items-center justify-between px-6 py-4">
          <Link to="/" className="text-2xl font-extrabold text-blue-700 tracking-tight flex items-center gap-2">
            <span className="bg-blue-600 text-white px-2 py-1 rounded mr-2">ChatSaaS</span>
            <span className="hidden sm:inline">Real-Time Chat</span>
          </Link>
          <nav className="flex gap-8 text-blue-700 font-medium text-lg">
            <Link className={pathname === "/" ? "underline" : "hover:underline"} to="/">Home</Link>
            <Link className={pathname === "/chat" ? "underline" : "hover:underline"} to="/chat">Chat</Link>
            <Link className={pathname === "/contact" ? "underline" : "hover:underline"} to="/contact">Contact</Link>
          </nav>
        </div>
      </header>
      <main className="flex-1 flex flex-col justify-center items-center px-2 py-8">
        {children}
      </main>
      <footer className="w-full text-center py-6 text-gray-400 border-t border-blue-100 mt-8">
        &copy; {new Date().getFullYear()} ChatSaaS. All rights reserved.
      </footer>
    </div>
  );
}
