import React, { useEffect, useRef, useState } from 'react';
import { useChat } from '../context/ChatContext';
import { io } from 'socket.io-client';
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { ScrollArea } from "./ui/scroll-area";
import { Users } from 'lucide-react';

let socket;

import Layout from "./Layout";

function ChatRoom({ exitRoom }) {
  const { user, roomKey, setUser, setRoomKey } = useChat();
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [userCount, setUserCount] = useState(1);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket = io('http://localhost:5002');
    socket.emit('join_room', { username: user, roomKey });
    socket.on('chat_history', msgs => setMessages(msgs));
    socket.on('receive_message', msg => setMessages(msgs => [...msgs, msg]));
    socket.on('user_joined', ({ userCount }) => setUserCount(userCount));
    socket.on('user_left', ({ userCount }) => setUserCount(userCount));
    return () => {
      socket.emit('leave_room', { roomKey, username: user });
      socket.disconnect();
    };
  }, [roomKey, user]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = e => {
    e.preventDefault();
    if (!input.trim()) return;
    socket.emit('send_message', { roomKey, username: user, message: input });
    setInput('');
  };

  const handleExit = () => {
    setUser(null);
    setRoomKey(null);
    exitRoom();
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-[calc(100vh-80px)] md:min-h-[calc(100vh-100px)] bg-gray-50 dark:bg-neutral-900 transition-colors duration-500 px-2">
      <div className="flex flex-col flex-1 w-full max-w-4xl min-h-[70vh] md:min-h-[80vh] bg-white dark:bg-neutral-800 rounded-2xl shadow-2xl mx-auto p-0 md:p-8">
        {/* Header Row */}
        <div className="flex flex-col md:flex-row items-center justify-between px-6 pt-6 pb-2 gap-4">
          <div className="font-bold text-xl md:text-2xl text-neutral-900 dark:text-white">Room Key: <span className="font-mono text-purple-600 dark:text-purple-300">{roomKey}</span></div>
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-2">
              <Users className="w-5 h-5 text-blue-500 dark:text-blue-300" />
              <span className="font-medium text-neutral-700 dark:text-neutral-200">{userCount}</span>
            </div>
            <Button onClick={handleExit} className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-md">Exit Chat</Button>
          </div>
        </div>
        {/* Banner/Notice */}
        <div className="text-center text-md md:text-lg font-medium mb-2 px-6 text-purple-600 dark:text-purple-300">No database. No chat storage. Anyone can chat, anytime.</div>
        {/* Chat Area */}
        <div className="flex-1 flex flex-col justify-end px-6 pb-4">
          <div className="bg-gray-50 dark:bg-neutral-700 rounded-lg p-4 mb-2 flex-1 min-h-[300px] max-h-[50vh] md:max-h-[60vh] overflow-y-auto">
            {messages.map((msg, idx) => (
              <div key={idx} className="mb-2">
                <span className={msg.type === 'system' ? "text-gray-400 dark:text-gray-500 italic" : "font-semibold text-blue-700 dark:text-blue-300"}>
                  {msg.username ? `${msg.username}: ` : ""}
                </span>
                <span className={msg.type === 'system' ? "text-gray-400 dark:text-gray-500 italic" : ""}>{msg.message}</span>
                <span className="ml-2 text-xs text-gray-400 dark:text-gray-500 align-bottom">{msg.timestamp && new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
          {/* Input Row */}
          <form onSubmit={sendMessage} className="flex gap-2 mt-2">
            <Input
              type="text"
              className="flex-1 rounded-lg border border-gray-300 dark:border-neutral-600 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400 dark:bg-neutral-800 dark:text-white"
              value={input}
              onChange={e => setInput(e.target.value)}
              placeholder="Type a message..."
              autoFocus
            />
            <Button type="submit" className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg">Send</Button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default ChatRoom;
