import React from "react";
import { Link, useLocation } from "react-router-dom";

import { useEffect } from "react";
export default function Layout({ children }) {
  const { pathname } = useLocation();
  // Set dark mode on initial load
  useEffect(() => {
    const stored = localStorage.getItem('theme');
    if (stored === 'dark') {
      document.documentElement.classList.add('dark');
    } else if (stored === 'light') {
      document.documentElement.classList.remove('dark');
    }
  }, []);
  return (
    <div className={`min-h-screen flex flex-col bg-gray-50 dark:bg-neutral-900 transition-colors duration-500`}>
      <header className="sticky top-0 z-30 w-full bg-white/80 dark:bg-neutral-900/90 backdrop-blur border-b border-blue-100 dark:border-neutral-800 shadow-sm">
        <div className="max-w-6xl mx-auto flex items-center justify-between px-6 py-4">
          <Link to="/" className="text-2xl font-extrabold text-transparent bg-clip-text bg-gradient-to-r from-blue-600 via-purple-600 to-pink-500 tracking-tight flex items-center gap-2 animate-fade-in">
            <span className="">AnonChat</span>
          </Link>
          <nav className="flex gap-8 text-blue-700 dark:text-purple-200 font-medium text-lg">
            <Link className={pathname === "/" ? "underline" : "hover:underline"} to="/">Home</Link>
            <Link className={pathname === "/chat" ? "underline" : "hover:underline"} to="/chat">Chat</Link>
            <Link className={pathname === "/contact" ? "underline" : "hover:underline"} to="/contact">Contact</Link>
          </nav>
          {/* Dark/Light Mode Toggle */}
          <button
            aria-label="Toggle dark mode"
            className="ml-6 p-2 rounded-full bg-gray-200 dark:bg-neutral-800 hover:bg-gray-300 dark:hover:bg-neutral-700 transition-colors shadow"
            onClick={() => {
              const root = window.document.documentElement;
              const isDark = root.classList.contains('dark');
              if (isDark) {
                root.classList.remove('dark');
                localStorage.setItem('theme', 'light');
              } else {
                root.classList.add('dark');
                localStorage.setItem('theme', 'dark');
              }
            }}
          >
            <span className="sr-only">Toggle dark mode</span>
            {/* Sun/Moon Icon */}
            <svg
              className="w-6 h-6 transition-transform duration-300"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            >
              <circle cx="12" cy="12" r="5" className="fill-yellow-400 dark:fill-transparent transition-colors" />
              <path
                className="dark:opacity-0"
                d="M12 1v2M12 21v2M4.22 4.22l1.42 1.42M18.36 18.36l1.42 1.42M1 12h2M21 12h2M4.22 19.78l1.42-1.42M18.36 5.64l1.42-1.42"
              />
              <path
                className="opacity-0 dark:opacity-100 transition-opacity"
                d="M21 12.79A9 9 0 1 1 11.21 3a7 7 0 0 0 9.79 9.79z"
              />
            </svg>
          </button>
        </div>
      </header>
      <div className="w-full text-center mt-6 animate-fade-in">
        <div className="text-xl font-semibold text-transparent bg-clip-text bg-gradient-to-r from-blue-700 via-purple-700 to-pink-600">
          No database. No chat storage. Anyone can chat, anytime.
        </div>
      </div>
      <main className="flex-1 flex flex-col justify-center items-center px-2 py-8 animate-fade-in">
        {children}
      </main>
      <footer className="w-full text-center py-6 text-gray-400 border-t border-blue-100 mt-8">
        &copy; {new Date().getFullYear()} AnonChat. All rights reserved.
      </footer>
      <style>{`
        .animate-gradient-x {
          background-size: 200% 200%;
          animation: gradient-x 8s ease-in-out infinite;
        }
        @keyframes gradient-x {
          0%, 100% { background-position: left center; }
          50% { background-position: right center; }
        }
        .animate-fade-in {
          animation: fadeIn 1.2s cubic-bezier(.39,.575,.565,1) both;
        }
        @keyframes fadeIn {
          0% { opacity: 0; transform: translateY(30px); }
          100% { opacity: 1; transform: none; }
        }
        .animate-bounce {
          animation: bounce 1.2s infinite;
        }
        @keyframes bounce {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-16px); }
        }
      `}</style>
    </div>
  );
}
