import React, { useEffect, useRef, useState } from 'react';
import { useChat } from '../context/ChatContext';
import { io } from 'socket.io-client';
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { ScrollArea } from "./ui/scroll-area";
import { Users } from 'lucide-react';

let socket;

import Layout from "./Layout";

function ChatRoom({ exitRoom }) {
  const { user, roomKey, setUser, setRoomKey } = useChat();
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [userCount, setUserCount] = useState(1);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket = io('http://localhost:5002');
    socket.emit('join_room', { username: user, roomKey });
    socket.on('chat_history', msgs => setMessages(msgs));
    socket.on('receive_message', msg => setMessages(msgs => [...msgs, msg]));
    socket.on('user_joined', ({ userCount }) => setUserCount(userCount));
    socket.on('user_left', ({ userCount }) => setUserCount(userCount));
    return () => {
      socket.emit('leave_room', { roomKey, username: user });
      socket.disconnect();
    };
  }, [roomKey, user]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = e => {
    e.preventDefault();
    if (!input.trim()) return;
    socket.emit('send_message', { roomKey, username: user, message: input });
    setInput('');
  };

  const handleExit = () => {
    setUser(null);
    setRoomKey(null);
    exitRoom();
  };

  return (
    <div className="flex flex-col h-screen w-full bg-[#ece5dd] ">
      {/* Header Bar */}
      <div className="flex items-center justify-between px-4 py-3 bg-[#25D366]  shadow text-white">
        <div className="font-bold text-lg md:text-xl flex items-center gap-3">
          <span>Room: <span className="font-mono bg-white/20 px-2 py-1 rounded select-all">{roomKey}</span></span>
          <button
            className="p-1 rounded hover:bg-white/20"
            title="Copy Room Key"
            type="button"
            onClick={() => navigator.clipboard.writeText(roomKey)}
          >
            <svg className="w-5 h-5" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><rect x="9" y="9" width="13" height="13" rx="2"/><path d="M5 15V5a2 2 0 0 1 2-2h10"/></svg>
          </button>
        </div>
        <div className="flex items-center gap-4">
          <Users className="w-5 h-5" />
          <span className="font-medium">{userCount}</span>
          <Button onClick={handleExit} className="ml-2 bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded">Exit</Button>
        </div>
      </div>
      {/* Chat Area */}
      <div className="flex-1 flex flex-col overflow-y-auto px-2 py-4 md:px-16 md:py-8 gap-2 bg-[#ece5dd]">
        {messages.map((msg, idx) => (
          msg.type === 'system' ? (
            <div key={idx} className="text-center text-gray-500 italic text-xs my-2 tracking-wide bg-white/70 rounded-full px-3 py-1 mx-auto w-fit shadow-sm">
              {msg.message} <span className="ml-2 text-[10px] text-gray-400">{msg.timestamp && new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
            </div>
          ) : (
            <div
              key={idx}
              className={`flex items-end gap-2 ${msg.username === user ? 'justify-end' : 'justify-start'}`}
            >
              {msg.username !== user && (
                <div className="flex-shrink-0 w-8 h-8 rounded-full bg-green-200 flex items-center justify-center font-bold text-green-900 text-lg shadow-sm">
                  {msg.username?.[0]?.toUpperCase() || '?'}
                </div>
              )}
              <div className={`relative group rounded-2xl px-4 py-2 max-w-[65vw] md:max-w-[55%] break-words shadow-md transition-all ${msg.username === user ? 'bg-[#25D366] text-white rounded-br-md self-end' : 'bg-white text-gray-800 rounded-bl-md self-start'} mb-1`}
                style={{ wordBreak: 'break-word' }}>
                <span className="block font-semibold text-xs mb-1 opacity-70">
                  {msg.username}
                </span>
                <span className="block text-base leading-snug">{msg.message}</span>
                <span className="absolute right-2 bottom-1 text-[10px] text-gray-500 opacity-80 group-hover:opacity-100 transition-opacity">
                  {msg.timestamp && new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                </span>
              </div>
              {msg.username === user && (
                <div className="flex-shrink-0 w-8 h-8 rounded-full bg-green-600 flex items-center justify-center font-bold text-white text-lg shadow-sm">
                  {user?.[0]?.toUpperCase() || '?'}
                </div>
              )}
            </div>
          )
        ))}
        <div ref={messagesEndRef} />
      </div>
      {/* Input Row */}
      <form onSubmit={sendMessage} className="flex items-center gap-2 p-4 bg-[#f7f7f7]  border-t border-[#ece5dd] ">
        <input
          type="text"
          className="flex-1 rounded-3xl border border-gray-300 dark:border-neutral-600 px-4 py-2 focus:outline-none focus:ring-2 focus:ring-[#25D366]   bg-white"
          value={input}
          onChange={e => setInput(e.target.value)}
          placeholder="Type a message..."
          autoFocus
        />
        <Button type="submit" className="bg-[#25D366] hover:bg-green-500 text-white px-6 py-2 rounded-3xl">Send</Button>
      </form>
    </div>
  );
}

export default ChatRoom;
