import React, { useEffect } from "react";

const svgBase64 =
  "data:image/png;base64,";

export default function WaveBackground() {
  useEffect(() => {
    let WIDTH = window.innerWidth;
    let HEIGHT = window.innerHeight;
    const WAVEHEIGHT = 60;
    const FREQUENCY = 200;
    const SPEED = 4;
    let xs = [];
    let tick = 0;

    function createWave() {
      xs = [];
      for (let i = 0; i <= WIDTH; i++) {
        xs.push(i);
      }
    }
    createWave();

    function animate() {
      let points = xs.map(x => {
        let y = HEIGHT / 2 + WAVEHEIGHT * Math.sin((x + tick) / FREQUENCY);
        return [x, y];
      });
      let path =
        "M" +
        points
          .map(p => p[0] + "," + p[1])
          .join(" L") +
        "L " + WIDTH + "," + HEIGHT + " L 0," + HEIGHT + " Z";
      const pathEl = document.getElementById("wave-path");
      if (pathEl) pathEl.setAttribute("d", path);
      tick += SPEED;
      requestAnimationFrame(animate);
    }
    animate();

    function handleResize() {
      WIDTH = window.innerWidth;
      HEIGHT = window.innerHeight;
      createWave();
    }
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <svg style={{position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', zIndex: 0, pointerEvents: 'none'}}>
      <defs>
        <pattern id="img1" patternUnits="userSpaceOnUse" width="100" height="200">
          <image xlinkHref={svgBase64} x="0" y="0" width="100" height="200" />
        </pattern>
      </defs>
      <path id="wave-path" fill="url(#img1)" />
    </svg>
  );
}
