import React, { useEffect, useRef, useState } from 'react';
import { useChat } from '../context/ChatContext';
import { io } from 'socket.io-client';
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { ScrollArea } from "./ui/scroll-area";
import { Users } from 'lucide-react';

let socket;

import Layout from "./Layout";

function ChatRoom({ exitRoom }) {
  const { user, roomKey, setUser, setRoomKey } = useChat();
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [userCount, setUserCount] = useState(1);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket = io('http://localhost:5002');
    socket.emit('join_room', { username: user, roomKey });
    socket.on('chat_history', msgs => setMessages(msgs));
    socket.on('receive_message', msg => setMessages(msgs => [...msgs, msg]));
    socket.on('user_joined', ({ userCount }) => setUserCount(userCount));
    socket.on('user_left', ({ userCount }) => setUserCount(userCount));
    return () => {
      socket.emit('leave_room', { roomKey, username: user });
      socket.disconnect();
    };
  }, [roomKey, user]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = e => {
    e.preventDefault();
    if (!input.trim()) return;
    socket.emit('send_message', { roomKey, username: user, message: input });
    setInput('');
  };

  const handleExit = () => {
    setUser(null);
    setRoomKey(null);
    exitRoom();
  };

  return (
    <div className="flex flex-col h-screen w-full bg-[#ece5dd] dark:bg-neutral-900">
      {/* Header Bar */}
      <div className="flex items-center justify-between px-4 py-3 bg-[#25D366] dark:bg-green-700 shadow text-white">
        <div className="font-bold text-lg md:text-xl flex items-center gap-3">
          <span>Room: <span className="font-mono bg-white/20 px-2 py-1 rounded select-all">{roomKey}</span></span>
          <button
            className="p-1 rounded hover:bg-white/20"
            title="Copy Room Key"
            type="button"
            onClick={() => navigator.clipboard.writeText(roomKey)}
          >
            <svg className="w-5 h-5" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><rect x="9" y="9" width="13" height="13" rx="2"/><path d="M5 15V5a2 2 0 0 1 2-2h10"/></svg>
          </button>
        </div>
        <div className="flex items-center gap-4">
          <Users className="w-5 h-5" />
          <span className="font-medium">{userCount}</span>
          <Button onClick={handleExit} className="ml-2 bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded">Exit</Button>
        </div>
      </div>
      {/* Chat Area */}
      <div className="flex-1 flex flex-col overflow-y-auto px-2 py-4 md:px-16 md:py-8 gap-2">
        {messages.map((msg, idx) => (
          msg.type === 'system' ? (
            <div key={idx} className="text-center text-gray-500 italic text-sm my-2">{msg.message} <span className="ml-2 text-xs">{msg.timestamp && new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span></div>
          ) : (
            <div
              key={idx}
              className={`flex ${msg.username === user ? 'justify-end' : 'justify-start'}`}
            >
              <div className={`rounded-2xl px-4 py-2 max-w-[70%] break-words shadow ${msg.username === user ? 'bg-[#dcf8c6] text-right dark:bg-green-900 dark:text-white' : 'bg-white text-left dark:bg-neutral-800 dark:text-white'} mb-1`}>
                <span className="block font-semibold text-green-700 dark:text-green-300 text-sm mb-1">{msg.username}</span>
                <span className="block text-base">{msg.message}</span>
                <span className="block text-xs text-gray-400 mt-1">{msg.timestamp && new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
              </div>
            </div>
          )
        ))}
        <div ref={messagesEndRef} />
      </div>
      {/* Input Row */}
      <form onSubmit={sendMessage} className="flex items-center gap-2 p-4 bg-[#f7f7f7] dark:bg-neutral-800 border-t border-[#ece5dd] dark:border-neutral-700">
        <input
          type="text"
          className="flex-1 rounded-3xl border border-gray-300 dark:border-neutral-600 px-4 py-2 focus:outline-none focus:ring-2 focus:ring-[#25D366] dark:bg-neutral-900 dark:text-white bg-white"
          value={input}
          onChange={e => setInput(e.target.value)}
          placeholder="Type a message..."
          autoFocus
        />
        <Button type="submit" className="bg-[#25D366] hover:bg-green-500 text-white px-6 py-2 rounded-3xl">Send</Button>
      </form>
    </div>
  );
}

export default ChatRoom;
