import React, { createContext, useContext, useState } from 'react';

const ChatContext = createContext();

export function ChatProvider({ children }) {
  const [user, setUserState] = useState(() => localStorage.getItem('anonchat_user') || null);
  const [roomKey, setRoomKeyState] = useState(() => localStorage.getItem('anonchat_roomKey') || null);

  // Persist user to localStorage
  const setUser = (val) => {
    setUserState(val);
    if (val) {
      localStorage.setItem('anonchat_user', val);
    } else {
      localStorage.removeItem('anonchat_user');
    }
  };

  // Persist roomKey to localStorage
  const setRoomKey = (val) => {
    setRoomKeyState(val);
    if (val) {
      localStorage.setItem('anonchat_roomKey', val);
    } else {
      localStorage.removeItem('anonchat_roomKey');
    }
  };

  return (
    <ChatContext.Provider value={{ user, setUser, roomKey, setRoomKey }}>
      {children}
    </ChatContext.Provider>
  );
}


export function useChat() {
  return useContext(ChatContext);
}
