import React, { useState } from 'react';
import { useChat } from '../context/ChatContext';

import Layout from "./Layout";

function HomeScreen({ enterRoom }) {
  const { setUser, setRoomKey } = useChat();
  const [showJoin, setShowJoin] = useState(false);
  const [showCreate, setShowCreate] = useState(false);
  const [username, setUsername] = useState('');
  const [roomInput, setRoomInput] = useState('');
  const [error, setError] = useState('');

  const handleCreateRoom = async () => {
    setError('');
    const res = await fetch('http://localhost:5002/create-room', { method: 'POST' });
    const data = await res.json();
    setRoomKey(data.roomKey);
    setShowCreate(true);
  };

  const handleJoinRoom = async () => {
    if (!username || !roomInput) {
      setError('Username and Room Key required');
      return;
    }
    // Validate room key exists
    const res = await fetch(`http://localhost:5002/validate-room/${roomInput}`);
    const data = await res.json();
    if (!data.exists) {
      setError('Room key does not exist!');
      return;
    }
    setUser(username);
    setRoomKey(roomInput);
    enterRoom();
  };

  return (
    <Layout>
      <section className="w-full max-w-md mx-auto flex flex-col items-center">
        <div className="bg-white/95 rounded-xl shadow-lg p-8 w-full">
          <h1 className="text-3xl font-extrabold text-blue-800 mb-6 text-center">Join or Create a Room</h1>
          <button
            className="w-full bg-blue-600 text-white py-2 rounded-lg mb-4 hover:bg-blue-700 font-semibold shadow"
            onClick={handleCreateRoom}
          >
            Create Room
          </button>
          <button
            className="w-full bg-green-600 text-white py-2 rounded-lg mb-4 hover:bg-green-700 font-semibold shadow"
            onClick={() => setShowJoin(true)}
          >
            Join Room
          </button>
          {showCreate && (
            <div className="mt-4 text-center">
              <div className="font-semibold text-blue-700">Room Created!</div>
              <div className="text-lg font-mono bg-gray-100 rounded p-2 mt-2 border inline-block">{useChat().roomKey}</div>
              <input
                className="mt-4 w-full border rounded px-2 py-2 text-lg"
                placeholder="Enter username"
                value={username}
                onChange={e => setUsername(e.target.value)}
              />
              <button
                className="w-full bg-blue-600 text-white py-2 rounded-lg mt-2 hover:bg-blue-700 font-semibold shadow"
                onClick={() => {
                  if (!username) {
                    setError('Username required');
                    return;
                  }
                  setUser(username);
                  enterRoom();
                }}
              >
                Enter Chat
              </button>
            </div>
          )}
          {showJoin && (
            <div className="mt-4">
              <input
                className="w-full border rounded px-2 py-2 mb-2 text-lg"
                placeholder="Enter username"
                value={username}
                onChange={e => setUsername(e.target.value)}
              />
              <input
                className="w-full border rounded px-2 py-2 mb-2 text-lg"
                placeholder="Enter room key"
                value={roomInput}
                onChange={e => setRoomInput(e.target.value)}
              />
              <button
                className="w-full bg-green-600 text-white py-2 rounded-lg hover:bg-green-700 font-semibold shadow"
                onClick={handleJoinRoom}
                type="button"
              >
                Join
              </button>
            </div>
          )}
          {error && <div className="text-red-500 mt-2 text-center font-medium">{error}</div>}
        </div>
      </section>
    </Layout>
  );
}


export default HomeScreen;
