import React, { useEffect, useRef, useState } from 'react';
import { useChat } from '../context/ChatContext';
import { io } from 'socket.io-client';
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { ScrollArea } from "./ui/scroll-area";
import { Users } from 'lucide-react';

let socket;

import Layout from "./Layout";

function ChatRoom({ exitRoom }) {
  const { user, roomKey, setUser, setRoomKey } = useChat();
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [userCount, setUserCount] = useState(1);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket = io('http://localhost:5002');
    socket.emit('join_room', { username: user, roomKey });
    socket.on('chat_history', msgs => setMessages(msgs));
    socket.on('receive_message', msg => setMessages(msgs => [...msgs, msg]));
    socket.on('user_joined', ({ userCount }) => setUserCount(userCount));
    socket.on('user_left', ({ userCount }) => setUserCount(userCount));
    return () => {
      socket.emit('leave_room', { roomKey, username: user });
      socket.disconnect();
    };
  }, [roomKey, user]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = e => {
    e.preventDefault();
    if (!input.trim()) return;
    socket.emit('send_message', { roomKey, username: user, message: input });
    setInput('');
  };

  const handleExit = () => {
    setUser(null);
    setRoomKey(null);
    exitRoom();
  };

  return (
    <Layout>
      <section className="w-full max-w-2xl mx-auto flex flex-col items-center">
        <Card className="w-full bg-white/95 shadow-lg border-blue-100">
          <CardHeader className="flex flex-row items-center justify-between pb-4">
            <div>
              <CardTitle className="text-xl flex items-center gap-2">
                Room Key: <span className="font-mono text-base bg-gray-100 rounded px-2 py-1">{roomKey}</span>
              </CardTitle>
            </div>
            <div className="flex items-center gap-2">
              <Users className="w-5 h-5 text-blue-500" />
              <span className="font-semibold">{userCount}</span>
              <Button className="ml-4 bg-red-500 hover:bg-red-600" type="button" onClick={handleExit}>Exit Chat</Button>
            </div>
          </CardHeader>
          <CardContent className="flex flex-col gap-4">
            <ScrollArea className="bg-gray-50 rounded p-3" style={{ minHeight: 300 }}>
              {messages.map((msg, i) => (
                <div key={i} className={
                  msg.type === 'system'
                    ? "text-center text-gray-500 italic text-sm my-2"
                    : msg.username === user
                    ? "text-right"
                    : "text-left"
                }>
                  {msg.type === 'system' ? (
                    <span>
                      {msg.message} <span className="ml-2 text-xs text-gray-400">{msg.timestamp && new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
                    </span>
                  ) : (
                    <>
                      <span className="font-bold text-blue-600">{msg.username}:</span> <span>{msg.message}</span>
                      <span className="ml-2 text-xs text-gray-400">{msg.timestamp && new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
                    </>
                  )}
                </div>
              ))}
              <div ref={messagesEndRef} />
            </ScrollArea>
            <form className="flex gap-2" onSubmit={sendMessage}>
              <Input
                placeholder="Type a message..."
                value={input}
                onChange={e => setInput(e.target.value)}
              />
              <Button type="submit">Send</Button>
            </form>
          </CardContent>
        </Card>
      </section>
    </Layout>
  );
}



export default ChatRoom;
